INSERT INTO `ghost` VALUES (1,'勇气之卫','YongQiZhiWei',1,-1,256,1,1,400,200,100,50,'主角专属魂侍，仁者之勇的化身，无论在什么情况下都能坚持自身的正义，面对强敌无所畏惧却又心怀仁义，可连续纵向攻击两次，持续一回合。','',1),(2,'天盾卫士','TianDunWeiShi',1,3,257,2,1,400,200,100,50,'袁铭志的专属魂侍。有着坚持而不放弃的忍受力，化身魂侍后能提升宿主侠客的防御力。全体攻击，降低敌方防御并减免己方伤害提升防御，持续一回合。','',1),(3,'绮梦花妖','QiMengHuaYao',1,4,258,4,1,400,200,100,50,'朱媛媛的专属魂侍。善于倾听自己，感受自己，能敞开胸怀感受自身和周围的一切。攻击敌方全体并晕眩，治疗己方，持续一回合。','',1),(4,'将臣','JiangChen',2,-1,259,0,1,400,200,100,50,'一将功成万骨枯。','',1),(5,'铳斗士','ChongDouShi',2,6,260,0,1,400,200,100,50,'古代的西部枪客。','',1),(6,'人鱼公主','RenYuGongZhu',2,5,261,0,1,400,200,100,50,'拥有天籁之声的海公主，歌声带给人希望，化身魂侍后能提升宿主侠客的攻击力。提升己方攻击力并攻击敌方降低防御，持续一回合。','',1),(7,'武圣','WuSheng',0,3,273,0,1,1500,400,200,100,'上古圣灵之一，与阴影的战斗永不休止。','',2),(8,'剑灵','GuDaiJianLing',0,-1,274,0,1,1500,400,200,100,'上古圣灵之一，与阴影的战斗永不休止。','',2),(9,'飞羽','FeiYu',0,4,275,0,1,1500,400,200,100,'上古圣灵之一，与阴影的战斗永不休止。','',2),(10,'阿修罗','AXiuLuo',0,-1,276,0,1,4000,1200,400,100,'霸者横栏。','AXiuLuo',2),(11,'洛神','LuoShen',0,5,277,0,1,1500,400,400,100,'女神的化身。','',2),(12,'木偶戏子','MuOXiZi',0,6,278,0,1,1500,400,400,100,'操纵木偶，捕猎阴影。','',2),(13,'武灵','WuLing',4,-1,312,0,1,1500,400,100,100,'传说武神的贴身护灵。','',1),(14,'梦灵','MengLing',4,6,313,0,1,400,200,100,100,'人生如戏，似梦非梦。','',1),(15,'灵泉侍女','LingQuanShiNv',4,5,315,0,1,400,200,100,100,'灵泉守护者。','',1),(16,'火凤仙子','HuoFengXianZi',4,4,316,0,1,400,200,100,100,'火凤女神的化身。','',1),(17,'影魔猎人','YingMoLieRen',4,3,314,0,1,400,200,100,100,'狩猎阴影，以阴影为食。','',1),(18,'九尾妖狐','JiuWeiYaoHu',5,-1,335,0,1,400,200,100,100,'青丘之山，有兽焉，其状如狐而九尾.','',1),(19,'神庙卫士','ShenMiaoWeiShi',5,4,336,0,1,400,200,100,100,'守护神庙的卫士。','',1),(20,'女妖','NvYao',5,3,337,0,1,400,200,100,100,'手为双剑的女侍魂。','',1),(22,'极寒冰巫','JiHanBingWu',5,5,338,0,1,400,300,100,50,'手持寒冰法杖的巫女。','',0),(23,'包黑天','BaoHeiTian',5,6,339,0,1,400,300,200,50,'深渊判官，只手遮天。','',0);
