CREATE TABLE `quest_activity_center` (
  `id` smallint(6) NOT NULL AUTO_INCREMENT,
  `relative` smallint(6) NOT NULL COMMENT '关联的活动',
  `weight` int(8) NOT NULL DEFAULT '0' COMMENT '活动权值',
  `name` varchar(60) NOT NULL COMMENT '活动名称(列表左侧)',
